function getOverlay(e){let t=e.target.closest(".about_leaders_grid_card"),r=t.querySelector(".leader_img_overlay");return r}function mouseOver(e){getOverlay(e).classList.remove("hidden")}function mouseOut(e){getOverlay(e).classList.add("hidden")}function getScreenWidth(){return window.innerWidth}function toggleOverlay(e){let t=e.target.closest(".about_leaders_grid_card"),r=t.querySelector(".leader_img_overlay");r.classList.toggle("hidden")}export function aboutInit(){let e=document.querySelectorAll(".about_leaders_grid_card"),t=getScreenWidth();t>991?e.forEach(e=>{e.addEventListener("mouseover",e=>toggleOverlay(e)),e.addEventListener("mouseout",e=>toggleOverlay(e))}):e.forEach(e=>{e.addEventListener("click",e=>toggleOverlay(e))})}